mapper.select:
	select count(1)
	from cr_credit u
	where cr_status!=-1

	and user_id=#{userId}


	and cr_status=#{crStatus}


	and create_date >=#{createTime}


	and cr_type=#{crType}


	and credit_type=#{creditType}


  	and	CONCAT(IFNULL(u.contact_name,''),IFNULL(u.debt_name,'')) like #{keyword}


		 from cr_credit u
		where cr_status!=-1

		and user_id=#{userId}


		and cr_status=#{crStatus}


		and create_date >=#{createTime}


		and cr_type=#{crType}


		and credit_type=#{creditType}


        and	CONCAT(IFNULL(u.contact_name,''),IFNULL(u.debt_name,'')) like #{keyword}

		order by u.id desc
		limit #{from},#{size}

